    public AspectBeanPostProcessor(HotPluggableAspect hotPluggableAspect) {
        this.hotPluggableAspect = hotPluggableAspect;
    }

    @Override
    public Object postProcessAfterInitialization(Object bean, String beanName) throws BeansException {
        if (bean instanceof Aspect) {
            hotPluggableAspect.addAspect((Aspect) bean);
        }
        return bean;
    }
}
